CREATE procedure [dbo].[GetCall_Cust_Details]

as
begin

SELECT Calls.Id, Calls.UserId, Calls.EngagementStatus, Calls.Description, 
	   Calls.NextCallDate, Register.firstName, Register.lastName, Register.companyName,
	   Register.address, Register.address2, Register.city, Register.province, Register.country,
	   Register.postalCode, Register.emailId, Register.contactNumber, Register.mobileNumber, Register.image
FROM   Calls INNER JOIN
             Register ON Calls.UserId = Register.Id 

end

